import React, { Component } from 'react'
import moment from 'moment'
import { Card, Row, Col, Badge, Modal, Spin, Icon } from 'antd'
import { Link } from 'react-router-dom'
import 'moment/locale/zh-cn'

// 七牛存储空间地址
const site = 'http://pvll4xfju.bkt.clouddn.com/'
const Meta = Card.Meta

moment.locale('zh-cn')

export default class Content extends Component {
  state = { visible: false }

  //关闭视频页面
  _handleClose = (e) => {
    if (this.player && this.player.pause) {
      this.player.pause()
    }
  }

  _handleCancel = (e) => {
    this.setState({
      visible: false
    })
  }

  _jumpToDetail = () => {
    console.log('开始调到详情页1', this.props)
    const { url } = this.props


    url && window.open(url)//直接打开新窗口跳转
    console.log('开始调到详情页2', url )
  }

  //打开视频播放弹窗
  _showModal = (movie) => {
    this.setState({
      visible: true
    })

    const video = site + movie.videoKey
    const pic = site + movie.coverKey

    if (!this.player) {
      setTimeout(() => {
        this.player = new DPlayer({
          container: document.getElementsByClassName('videoModal')[0],
          screenshot: true,
          autoplay: true,
          video: {
            url: video,
            pic: pic,
            thumbnails: pic
          }
        })
      }, 500)
    } else {
      if (this.player.video.currentSrc !== video) {
        this.player.switchVideo({
          url: video,
          autoplay: true,
          pic: pic,
          type: 'auto'
        })
      }

      this.player.play()
    }
  }

  _renderContent = () => {
    const { movies } = this.props  //从home的index传过来的就只有movies

    return (
      <div style={{ padding: '30px' }}>
        <Row>
          {
            movies.map((it, i) => (
              <Col
                key={i}
                xl={{span: 6}}
                lg={{span: 8}}
                md={{span: 12}}
                sm={{span: 24}}
                style={{marginBottom: '8px'}}
              >
                <Card
                  bordered={false}
                  hoverable
                  style={{ width: '100%' }}
                  // 配置card底部的说明图标和文字部分
                  actions={[
                    <Badge>
                      <Icon style={{marginRight: '2px'}} type='clock-circle' />
                      {moment(it.meta.createdAt).fromNow(true)} 前更新
                    </Badge>,
                    <Badge>
                      <Icon style={{marginRight: '2px'}} type='star' />
                      {it.rate} 分
                    </Badge>
                  ]}
                  // 七牛的工具对图片进行处理,cover可以跳转到视频预览
                  cover = { 
                    < img onClick = { () => this._showModal(it) }
                          src = { site + it.posterKey + '?imageMogr2/thumbnail/x1680/crop/1080x1600' } />}
                >
                  {/* 整个card的文字说明部分,可以跳到详情页 */}
                  <Meta
                    style={{height: '202px', overflow: 'hidden'}}
                    title={<Link to={`/detail/${it._id}`}>{it.title}</Link>}
                    onClick={this._jumpToDetail}//这个跳转是没有起作用.没有传入url
                    description={<Link to={`/detail/${it._id}`}>{it.summary}</Link>}
                   />
                </Card>
              </Col>
            ))
          }
        </Row>
        <Modal
          className='videoModal'
          footer={null}
          visible={this.state.visible}
          afterClose={this._handleClose}
          onCancel={this._handleCancel}
        >
          <Spin size='large' />
        </Modal>
      </div>
    )
  }

  render () {
    return (
      <div style={{ padding: 10 }}>
        {this._renderContent()}
      </div>
    )
  }
}
